"use client"

import { GooeyToaster as GooeyToasterPrimitive, gooeyToast } from "gooey-ui"
import type { GooeyToasterProps } from "gooey-ui"
import "gooey-ui/styles.css"

export { gooeyToast }
export type { GooeyToasterProps }
export type {
  GooeyToastOptions,
  GooeyPromiseData,
  GooeyToastAction,
  GooeyToastClassNames,
  GooeyToastTimings,
} from "gooey-ui"

function GooeyToaster(props: GooeyToasterProps) {
  return <GooeyToasterPrimitive position="bottom-right" {...props} />
}

export { GooeyToaster }
