"use client"

import { GoeyToaster as GoeyToasterPrimitive, toast } from "@gooey-ui/core"
import type { GoeyToasterProps } from "@gooey-ui/core"
import "@gooey-ui/core/styles.css"

export { toast }
export type { GoeyToasterProps }
export type {
  ToastVariant,
  ToastPosition,
  ToastAction,
  ToastClassNames,
  AnimationPreset,
} from "@gooey-ui/core"

function GooeyToaster(props: GoeyToasterProps) {
  return <GoeyToasterPrimitive position="bottom-right" {...props} />
}

export { GooeyToaster }
